mapper.select:
select tb_mainwps.FID fid,tb_mainwps.FWPSNum fwpsnum,tb_mainwps.Fweld_I fweld_i,tb_mainwps.Fweld_V fweld_v,tb_mainwps.Fweld_I_MAX fweld_i_max,tb_mainwps.Fweld_I_MIN fweld_i_min,tb_mainwps.Fweld_V_MAX fweld_v_max,tb_mainwps.Fweld_V_MIN fweld_v_min,tb_mainwps.Fweld_Alter_I fweld_alter_i,tb_mainwps.Fweld_Alter_V fweld_alter_v,tb_mainwps.Fweld_PreChannel fweld_prechannel,tb_mainwps.FCReateDate fcreatedate,tb_mainwps.FUpdateDate fupdatedate,tb_mainwps.Fowner fowner,tb_mainwps.Fback fback,tb_mainwps.Fname fname,tb_mainwps.Fdiameter fdiameter,i.fid insid,i.fname insname from tb_mainwps LEFT join tb_insframework i on tb_mainwps.Fowner=i.fid LEFT JOIN tb_insframework ins on ins.fid = i.fparent LEFT JOIN tb_insframework insf on insf.fid = ins.fparent where 1=1 ,s.fspeed,s.farc_speed,s.farc_tuny_speed,s.fselectstep and i.fid=#{parent} or ins.fid=#{parent} or insf.fid=#{parent} or insf.fparent=#{parent}
and ${str}
ORDER by tb_mainwps.FID

select s.`fid` fid,s.`fspe_num` welderid,s.finitial finitial,s.fcontroller fcontroller,s.fselect insname,s.farc weldername,s.fcharacter fweld_v_max,s.fmode fmode,s.fmaterial updatename,s.fgas fback,s.fweldingratio weldingratio,s.fdiameter fname,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter,s.ffrequency frequency,s.fgasflow gasflow,s.fwater_cooled_torch ftorch,fwelding_process fprocessid, point_speed,pulse_ratio,thrust_ele,decline_time,rise_time,fweldparameters,pulse,arc_length,manual_weld,fargon,Base_vol1,Base_vol,Base_ele,hand_ele,Rush,handarc_time,handarc_ele,farc_time,firsttime,guide,slope,specialarc,specialarc_rep,ts_condition,pulse_ele,ac_frequency,clean_width,ac_dc,pulse_width,ac_ratio, ac_wave,pulse_tuny_ele,special_arcorder,special_arc_initial,special_arctime,click_ele,two_click_ele,repeat_end,s.fspeed,s.farc_speed,s.farc_tuny_speed,s.fselectstep FROM tb_specification s where 1=1 AND s.`fmachine_id`=#{machine}
AND s.`fspe_num`=#{chanel}
ORDER by s.fid

select s.`fid` fid,s.`fspe_num` fweld_i_max,s.finitial fweld_i_min,s.fcontroller fweld_alter_i,s.fselect fweld_i,s.farc fweld_v,s.fcharacter fweld_v_max,s.fmode fweld_v_min,s.fmaterial fweld_prechannel,s.fgas fweld_alter_v,s.fdiameter insid,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter,s.ffrequency frequency,s.fgasflow gasflow,s.fwater_cooled_torch ftorch,fwelding_process fprocessid,
point_speed,pulse_ratio,thrust_ele,decline_time,rise_time,fweldparameters,pulse,arc_length,manual_weld,fargon,Base_vol1,Base_vol,Base_ele,hand_ele,Rush,handarc_time,handarc_ele,farc_time,firsttime,s.fselectstep,s.fspeed,s.farc_tuny_speed,s.farc_speed,pulse_ele,ac_frequency,clean_width,ac_dc,pulse_width,ac_ratio,ac_wave,pulse_tuny_ele,special_arcorder,special_arc_initial,special_arctime,click_ele,two_click_ele,repeat_end,guide,slope,specialarc,specialarc_rep,ts_condition 
FROM tb_specification s 
where 1=1 AND s.`fmachine_id`=#{machine}

  AND s.`fspe_num`=#{chanel}

ORDER by s.fid

select s.`fid` fid,s.`fspe_num` fweld_i_max,s.finitial fweld_i_min,s.fcontroller fweld_alter_i,s.fselect fweld_i,s.farc fweld_v,s.fcharacter fweld_v_max,s.fmode fweld_v_min,s.fmaterial fweld_prechannel,s.fgas fweld_alter_v,s.fdiameter insid,s.ftime,s.fhysteresis,s.fadvance,s.fini_ele ,s.fini_vol,s.fini_vol1,s.fweld_ele,s.fweld_vol,s.fweld_vol1,s.farc_ele,s.farc_vol,s.farc_vol1,s.fweld_tuny_ele,s.fweld_tuny_vol,s.farc_tuny_ele,s.farc_tuny_vol fdiameter,s.ffrequency,s.fgasflow gasflow,s.fwater_cooled_torch ftorch,fwelding_process fprocessid,
point_speed,pulse_ratio,thrust_ele,decline_time,rise_time,fweldparameters,pulse,arc_length,manual_weld,fargon,Base_vol1,Base_vol,Base_ele,hand_ele,Rush,handarc_time,handarc_ele,farc_time,firsttime,s.fselectstep,s.fspeed,s.farc_tuny_speed,s.farc_speed,s.fini_tuny_vol,s.farc_tuny_vol  
FROM tb_specification s 
where 1=1 AND s.`fmachine_id`=#{machine}

	AND s.`fspe_num`=#{chanel}

ORDER by s.fid

select tb_mainwps.FWPSNum fwpsnum,tb_welding_machine.fequipment_no fname,tb_wps_welddevices.Fchannel fweld_prechannel,tb_wps_welddevices.FCReateDate fcreatedate from tb_wps_welddevices left join tb_mainwps on tb_wps_welddevices.FWeldWPSID=tb_mainwps.FID left join tb_welding_machine on tb_wps_welddevices.FWeldDevicesID=tb_welding_machine.fid where 1=1

	and tb_insframework.fid = #{parent}

ORDER by tb_wps_welddevices.FCReateDate desc

select s.* FROM tb_specification s 
where 1=1 AND s.fmachine_id=#{machine}

	AND s.fspe_num=#{chanel}

ORDER by s.fid

select s.fspe_num fwpsnum,s.f024 FROM tb_specification s 
where 1=1 AND s.fmachine_id=#{machine}
ORDER by s.fspe_num
